<span class="line"><span style="color:#A6ACCD;">    </span><span style="color:#C792EA;">SpringApplication</span><span style="color:#A6ACCD;"> springApplication </span><span style="color:#89DDFF;">=</span><span style="color:#A6ACCD;"> </span><span style="color:#89DDFF;font-style:italic;">new</span><span style="color:#A6ACCD;"> </span><span style="color:#82AAFF;">SpringApplication</span><span style="color:#89DDFF;">(</span><span style="color:#A6ACCD;">DemoApplication</span><span style="color:#89DDFF;">.</span><span style="color:#A6ACCD;">class</span><span style="color:#89DDFF;">);</span></span>
