<span class="line"><span style="color:#A6ACCD;">result</span><span style="color:#89DDFF;">=</span><span style="color:#89DDFF;">\`</span><span style="color:#FFCB6B;">expr</span><span style="color:#C3E88D;"> </span><span style="color:#A6ACCD;">\\(</span><span style="color:#C3E88D;"> </span><span style="color:#F78C6C;">10</span><span style="color:#C3E88D;"> + </span><span style="color:#F78C6C;">10</span><span style="color:#C3E88D;"> </span><span style="color:#A6ACCD;">\\)</span><span style="color:#C3E88D;"> </span><span style="color:#A6ACCD;">\\*</span><span style="color:#C3E88D;"> </span><span style="color:#F78C6C;">2</span><span style="color:#C3E88D;"> + </span><span style="color:#F78C6C;">100</span><span style="color:#89DDFF;">\`</span></span>
